PRAGMA
foreign_keys=OFF;
BEGIN
TRANSACTION;
CREATE TABLE helptext

(

    id       INTEGER not null
        primary key autoincrement
        unique,

    command  TEXT,

    created  INTEGER,

    updated  INTEGER,

    help     TEXT,

    decr     TEXT,

    example  TEXT,

    parsed   TEXT,

    source   TEXT,

    device   TEXT,

    dop_info TEXT,

    weight   INTEGER

);
INSERT INTO helptext
VALUES (100, 'cat', 1610301250, 1610301250, replace(
        'Usage: cat [OPTION]... [FILE]...\nConcatenate FILE(s) to standard output.\n\nWith no FILE, or when FILE is -, read standard input.\n\n  -A, --show-all           equivalent to -vET\n  -b, --number-nonblank    number nonempty output lines, overrides -n\n  -e                       equivalent to -vE\n  -E, --show-ends          display $ at end of each line\n  -n, --number             number all output lines\n  -s, --squeeze-blank      suppress repeated empty output lines\n  -t                       equivalent to -vT\n  -T, --show-tabs          display TAB characters as ^I\n  -u                       (ignored)\n  -v, --show-nonprinting   use ^ and M- notation, except for LFD and TAB\n      --help     display this help and exit\n      --version  output version information and exit\n\nExamples:\n  cat f - g  Output f''s contents, then standard input, then g''s contents.\n  cat        Copy standard input to standard output.\n\nGNU coreutils online help: <https://www.gnu.org/software/coreutils/>\nReport any translation bugs to <https://translationproject.org/team/>\nFull documentation <https://www.gnu.org/software/coreutils/cat>\nor available locally via: info ''(coreutils) cat invocation''\n',
        '\n', char(10)), 'Вывести содержимое файла (или файлов).', replace(replace(
                                                                                   '<br>\r\n<pre><code class="plaintext hljs">[user@testhost ~]$ cat temp\r\nContent of a file.\r\nLalalala...\r\n</code></pre><br>\r\nТакже стоит обратить внимание на команды <i>head</i> (вывести <i>n</i> первых строк или байт файла) и <i>tail</i> (о ней — далее).<br>\r\n<br>',
                                                                                   '\r', char(13)), '\n', char(10)),
        replace(
                'Usage: cat [OPTION]... [FILE]...\nConcatenate FILE(s) to standard output.\n\nWith no FILE, or when FILE is -, read standard input.\n\n  -A, --show-all           equivalent to -vET\n  -b, --number-nonblank    number nonempty output lines, overrides -n\n  -e                       equivalent to -vE\n  -E, --show-ends          display $ at end of each line\n  -n, --number             number all output lines\n  -s, --squeeze-blank      suppress repeated empty output lines\n  -t                       equivalent to -vT\n  -T, --show-tabs          display TAB characters as ^I\n  -u                       (ignored)\n  -v, --show-nonprinting   use ^ and M- notation, except for LFD and TAB\n      --help     display this help and exit\n      --version  output version information and exit\n\nExamples:\n  cat f - g  Output f''s contents, then standard input, then g''s contents.\n  cat        Copy standard input to standard output.\n\nGNU coreutils online help: <https://www.gnu.org/software/coreutils/>\nReport any translation bugs to <https://translationproject.org/team/>\nFull documentation <https://www.gnu.org/software/coreutils/cat>\nor available locally via: info ''(coreutils) cat invocation''\n',
                '\n', char(10)), 'cat --help', 'Notebook',
        '{"decr_url":"https:\/\/habr.com\/ru\/post\/481398\/","parsed":1610301250,"from_file":"..\/..\/helprus.txt","executor":"passthru"}',
        10);
INSERT INTO helptext
VALUES (101, 'cd', 1610301250, 1610301250, '', 'Изменить текущую директорию.', replace(replace(
                                                                                               '<br>\r\n<pre><code class="plaintext hljs">[user@testhost ~]$ pwd\r\n/home/user\r\n[user@testhost ~]$ cd /home/\r\n[user@testhost home]$ pwd\r\n/home\r\n</code></pre><br>\r\nЕсли не передавать имя директории в качестве аргумента, будет использоваться переменная окружения <i>$HOME</i>, то есть домашняя директория. Также может быть удобно использовать `<i>~</i>` — специальный символ, означающий <i>$HOME</i>:<br>\r\n<br>\r\n<pre><code class="plaintext hljs">[user@testhost etc]$ pwd\r\n/etc\r\n[user@testhost etc]$ cd ~/test/\r\n[user@testhost test]$ pwd\r\n/home/user/test\r\n</code></pre><br>',
                                                                                               '\r', char(13)),
                                                                                       '\n', char(10)), '', 'cd --help',
        'Notebook',
        '{"decr_url":"https:\/\/habr.com\/ru\/post\/481398\/","parsed":1610301250,"from_file":"..\/..\/helprus.txt","executor":"passthru"}',
        5);
INSERT INTO helptext
VALUES (102, 'cp', 1610301250, 1610301250, replace(
        'Usage: cp [OPTION]... [-T] SOURCE DEST\n  or:  cp [OPTION]... SOURCE... DIRECTORY\n  or:  cp [OPTION]... -t DIRECTORY SOURCE...\nCopy SOURCE to DEST, or multiple SOURCE(s) to DIRECTORY.\n\nMandatory arguments to long options are mandatory for short options too.\n  -a, --archive                same as -dR --preserve=all\n      --attributes-only        don''t copy the file data, just the attributes\n      --backup[=CONTROL]       make a backup of each existing destination file\n  -b                           like --backup but does not accept an argument\n      --copy-contents          copy contents of special files when recursive\n  -d                           same as --no-dereference --preserve=links\n  -f, --force                  if an existing destination file cannot be\n                                 opened, remove it and try again (this option\n                                 is ignored when the -n option is also used)\n  -i, --interactive            prompt before overwrite (overrides a previous -n\n                                  option)\n  -H                           follow command-line symbolic links in SOURCE\n  -l, --link                   hard link files instead of copying\n  -L, --dereference            always follow symbolic links in SOURCE\n  -n, --no-clobber             do not overwrite an existing file (overrides\n                                 a previous -i option)\n  -P, --no-dereference         never follow symbolic links in SOURCE\n  -p                           same as --preserve=mode,ownership,timestamps\n      --preserve[=ATTR_LIST]   preserve the specified attributes (default:\n                                 mode,ownership,timestamps), if possible\n                                 additional attributes: context, links, xattr,\n                                 all\n      --no-preserve=ATTR_LIST  don''t preserve the specified attributes\n      --parents                use full source file name under DIRECTORY\n  -R, -r, --recursive          copy directories recursively\n      --reflink[=WHEN]         control clone/CoW copies. See below\n      --remove-destination     remove each existing destination file before\n                                 attempting to open it (contrast with --force)\n      --sparse=WHEN            control creation of sparse files. See below\n      --strip-trailing-slashes  remove any trailing slashes from each SOURCE\n                                 argument\n  -s, --symbolic-link          make symbolic links instead of copying\n  -S, --suffix=SUFFIX          override the usual backup suffix\n  -t, --target-directory=DIRECTORY  copy all SOURCE arguments into DIRECTORY\n  -T, --no-target-directory    treat DEST as a normal file\n  -u, --update                 copy only when the SOURCE file is newer\n                                 than the destination file or when the\n                                 destination file is missing\n  -v, --verbose                explain what is being done\n  -x, --one-file-system        stay on this file system\n  -Z                           set SELinux security context of destination\n                                 file to default type\n      --context[=CTX]          like -Z, or if CTX is specified then set the\n                                 SELinux or SMACK security context to CTX\n      --help     display this help and exit\n      --version  output version information and exit\n\nBy default, sparse SOURCE files are detected by a crude heuristic and the\ncorresponding DEST file is made sparse as well.  That is the behavior\nselected by --sparse=auto.  Specify --sparse=always to create a sparse DEST\nfile whenever the SOURCE file contains a long enough sequence of zero bytes.\nUse --sparse=never to inhibit creation of sparse files.\n\nWhen --reflink[=always] is specified, perform a lightweight copy, where the\ndata blocks are copied only when modified.  If this is not possible the copy\nfails, or if --reflink=auto is specified, fall back to a standard copy.\nUse --reflink=never to ensure a standard copy is performed.\n\nThe backup suffix is ''~'', unless set with --suffix or SIMPLE_BACKUP_SUFFIX.\nThe version control method may be selected via the --backup option or through\nthe VERSION_CONTROL environment variable.  Here are the values:\n\n  none, off       never make backups (even if --backup is given)\n  numbered, t     make numbered backups\n  existing, nil   numbered if numbered backups exist, simple otherwise\n  simple, never   always make simple backups\n\nAs a special case, cp makes a backup of SOURCE when the force and backup\noptions are given and SOURCE and DEST are the same name for an existing,\nregular file.\n\nGNU coreutils online help: <https://www.gnu.org/software/coreutils/>\nReport any translation bugs to <https://translationproject.org/team/>\nFull documentation <https://www.gnu.org/software/coreutils/cp>\nor available locally via: info ''(coreutils) cp invocation''\n',
        '\n', char(10)), 'Копировать файл или директорию.', replace(replace(
                                                                            '<br>\r\n<pre><code class="plaintext hljs">[user@testhost ~]$ ls\r\ntemp&nbsp; test\r\n[user@testhost ~]$ cp temp temp_clone\r\n[user@testhost ~]$ ls\r\ntemp&nbsp; temp_clone&nbsp; test\r\n</code></pre><br>\r\nУ этой команды также есть опции <i>-r</i> и <i>-f</i>, их можно использовать, чтобы гарантированно скопировать иерархию директорий и папок в другое место.<br>\r\n<br>',
                                                                            '\r', char(13)), '\n', char(10)), replace(
                'Usage: cp [OPTION]... [-T] SOURCE DEST\n  or:  cp [OPTION]... SOURCE... DIRECTORY\n  or:  cp [OPTION]... -t DIRECTORY SOURCE...\nCopy SOURCE to DEST, or multiple SOURCE(s) to DIRECTORY.\n\nMandatory arguments to long options are mandatory for short options too.\n  -a, --archive                same as -dR --preserve=all\n      --attributes-only        don''t copy the file data, just the attributes\n      --backup[=CONTROL]       make a backup of each existing destination file\n  -b                           like --backup but does not accept an argument\n      --copy-contents          copy contents of special files when recursive\n  -d                           same as --no-dereference --preserve=links\n  -f, --force                  if an existing destination file cannot be\n                                 opened, remove it and try again (this option\n                                 is ignored when the -n option is also used)\n  -i, --interactive            prompt before overwrite (overrides a previous -n\n                                  option)\n  -H                           follow command-line symbolic links in SOURCE\n  -l, --link                   hard link files instead of copying\n  -L, --dereference            always follow symbolic links in SOURCE\n  -n, --no-clobber             do not overwrite an existing file (overrides\n                                 a previous -i option)\n  -P, --no-dereference         never follow symbolic links in SOURCE\n  -p                           same as --preserve=mode,ownership,timestamps\n      --preserve[=ATTR_LIST]   preserve the specified attributes (default:\n                                 mode,ownership,timestamps), if possible\n                                 additional attributes: context, links, xattr,\n                                 all\n      --no-preserve=ATTR_LIST  don''t preserve the specified attributes\n      --parents                use full source file name under DIRECTORY\n  -R, -r, --recursive          copy directories recursively\n      --reflink[=WHEN]         control clone/CoW copies. See below\n      --remove-destination     remove each existing destination file before\n                                 attempting to open it (contrast with --force)\n      --sparse=WHEN            control creation of sparse files. See below\n      --strip-trailing-slashes  remove any trailing slashes from each SOURCE\n                                 argument\n  -s, --symbolic-link          make symbolic links instead of copying\n  -S, --suffix=SUFFIX          override the usual backup suffix\n  -t, --target-directory=DIRECTORY  copy all SOURCE arguments into DIRECTORY\n  -T, --no-target-directory    treat DEST as a normal file\n  -u, --update                 copy only when the SOURCE file is newer\n                                 than the destination file or when the\n                                 destination file is missing\n  -v, --verbose                explain what is being done\n  -x, --one-file-system        stay on this file system\n  -Z                           set SELinux security context of destination\n                                 file to default type\n      --context[=CTX]          like -Z, or if CTX is specified then set the\n                                 SELinux or SMACK security context to CTX\n      --help     display this help and exit\n      --version  output version information and exit\n\nBy default, sparse SOURCE files are detected by a crude heuristic and the\ncorresponding DEST file is made sparse as well.  That is the behavior\nselected by --sparse=auto.  Specify --sparse=always to create a sparse DEST\nfile whenever the SOURCE file contains a long enough sequence of zero bytes.\nUse --sparse=never to inhibit creation of sparse files.\n\nWhen --reflink[=always] is specified, perform a lightweight copy, where the\ndata blocks are copied only when modified.  If this is not possible the copy\nfails, or if --reflink=auto is specified, fall back to a standard copy.\nUse --reflink=never to ensure a standard copy is performed.\n\nThe backup suffix is ''~'', unless set with --suffix or SIMPLE_BACKUP_SUFFIX.\nThe version control method may be selected via the --backup option or through\nthe VERSION_CONTROL environment variable.  Here are the values:\n\n  none, off       never make backups (even if --backup is given)\n  numbered, t     make numbered backups\n  existing, nil   numbered if numbered backups exist, simple otherwise\n  simple, never   always make simple backups\n\nAs a special case, cp makes a backup of SOURCE when the force and backup\noptions are given and SOURCE and DEST are the same name for an existing,\nregular file.\n\nGNU coreutils online help: <https://www.gnu.org/software/coreutils/>\nReport any translation bugs to <https://translationproject.org/team/>\nFull documentation <https://www.gnu.org/software/coreutils/cp>\nor available locally via: info ''(coreutils) cp invocation''\n',
                '\n', char(10)), 'cp --help', 'Notebook',
        '{"decr_url":"https:\/\/habr.com\/ru\/post\/481398\/","parsed":1610301250,"from_file":"..\/..\/helprus.txt","executor":"passthru"}',
        8);
INSERT INTO helptext
VALUES (103, 'curl', 1610301251, 1610301251, replace(replace(
                                                             'Usage: curl [options...] <url>\r\n     --abstract-unix-socket <path> Connect via abstract Unix domain socket\r\n     --anyauth       Pick any authentication method\r\n -a, --append        Append to target file when uploading\r\n     --basic         Use HTTP Basic Authentication\r\n     --cacert <CA certificate> CA certificate to verify peer against\r\n     --capath <dir>  CA directory to verify peer against\r\n -E, --cert <certificate[:password]> Client certificate file and password\r\n     --cert-status   Verify the status of the server certificate\r\n     --cert-type <type> Certificate file type (DER/PEM/ENG)\r\n     --ciphers <list of ciphers> SSL ciphers to use\r\n     --compressed    Request compressed response\r\n -K, --config <file> Read config from a file\r\n     --connect-timeout <seconds> Maximum time allowed for connection\r\n     --connect-to <HOST1:PORT1:HOST2:PORT2> Connect to host\r\n -C, --continue-at <offset> Resumed transfer offset\r\n -b, --cookie <data> Send cookies from string/file\r\n -c, --cookie-jar <filename> Write cookies to <filename> after operation\r\n     --create-dirs   Create necessary local directory hierarchy\r\n     --crlf          Convert LF to CRLF in upload\r\n     --crlfile <file> Get a CRL list in PEM format from the given file\r\n -d, --data <data>   HTTP POST data\r\n     --data-ascii <data> HTTP POST ASCII data\r\n     --data-binary <data> HTTP POST binary data\r\n     --data-raw <data> HTTP POST data, ''@'' allowed\r\n     --data-urlencode <data> HTTP POST data url encoded\r\n     --delegation <LEVEL> GSS-API delegation permission\r\n     --digest        Use HTTP Digest Authentication\r\n -q, --disable       Disable .curlrc\r\n     --disable-eprt  Inhibit using EPRT or LPRT\r\n     --disable-epsv  Inhibit using EPSV\r\n     --dns-interface <interface> Interface to use for DNS requests\r\n     --dns-ipv4-addr <address> IPv4 address to use for DNS requests\r\n     --dns-ipv6-addr <address> IPv6 address to use for DNS requests\r\n     --dns-servers <addresses> DNS server addrs to use\r\n -D, --dump-header <filename> Write the received headers to <filename>\r\n     --egd-file <file> EGD socket path for random data\r\n     --engine <name> Crypto engine to use\r\n     --expect100-timeout <seconds> How long to wait for 100-continue\r\n -f, --fail          Fail silently (no output at all) on HTTP errors\r\n     --fail-early    Fail on first transfer error, do not continue\r\n     --false-start   Enable TLS False Start\r\n -F, --form <name=content> Specify HTTP multipart POST data\r\n     --form-string <name=string> Specify HTTP multipart POST data\r\n     --ftp-account <data> Account data string\r\n     --ftp-alternative-to-user <command> String to replace USER [name]\r\n     --ftp-create-dirs Create the remote dirs if not present\r\n     --ftp-method <method> Control CWD usage\r\n     --ftp-pasv      Use PASV/EPSV instead of PORT\r\n -P, --ftp-port <address> Use PORT instead of PASV\r\n     --ftp-pret      Send PRET before PASV\r\n     --ftp-skip-pasv-ip Skip the IP address for PASV\r\n     --ftp-ssl-ccc   Send CCC after authenticating\r\n     --ftp-ssl-ccc-mode <active/passive> Set CCC mode\r\n     --ftp-ssl-control Require SSL/TLS for FTP login, clear for transfer\r\n -G, --get           Put the post data in the URL and use GET\r\n -g, --globoff       Disable URL sequences and ranges using {} and []\r\n -I, --head          Show document info only\r\n -H, --header <header/@file> Pass custom header(s) to server\r\n -h, --help          This help text\r\n     --hostpubmd5 <md5> Acceptable MD5 hash of the host public key\r\n -0, --http1.0       Use HTTP 1.0\r\n     --http1.1       Use HTTP 1.1\r\n     --http2         Use HTTP 2\r\n     --http2-prior-knowledge Use HTTP 2 without HTTP/1.1 Upgrade\r\n     --ignore-content-length Ignore the size of the remote resource\r\n -i, --include       Include protocol response headers in the output\r\n -k, --insecure      Allow insecure server connections when using SSL\r\n     --interface <name> Use network INTERFACE (or address)\r\n -4, --ipv4          Resolve names to IPv4 addresses\r\n -6, --ipv6          Resolve names to IPv6 addresses\r\n -j, --junk-session-cookies Ignore session cookies read from file\r\n     --keepalive-time <seconds> Interval time for keepalive probes\r\n     --key <key>     Private key file name\r\n     --key-type <type> Private key file type (DER/PEM/ENG)\r\n     --krb <level>   Enable Kerberos with security <level>\r\n     --libcurl <file> Dump libcurl equivalent code of this command line\r\n     --limit-rate <speed> Limit transfer speed to RATE\r\n -l, --list-only     List only mode\r\n     --local-port <num/range> Force use of RANGE for local port numbers\r\n -L, --location      Follow redirects\r\n     --location-trusted Like --location, and send auth to other hosts\r\n     --login-options <options> Server login options\r\n     --mail-auth <address> Originator address of the original email\r\n     --mail-from <address> Mail from this address\r\n     --mail-rcpt <address> Mail from this address\r\n -M, --manual        Display the full manual\r\n     --max-filesize <bytes> Maximum file size to download\r\n     --max-redirs <num> Maximum number of redirects allowed\r\n -m, --max-time <time> Maximum time allowed for the transfer\r\n     --metalink      Process given URLs as metalink XML file\r\n     --negotiate     Use HTTP Negotiate (SPNEGO) authentication\r\n -n, --netrc         Must read .netrc for user name and password\r\n     --netrc-file <filename> Specify FILE for netrc\r\n     --netrc-optional Use either .netrc or URL\r\n -:, --next          Make next URL use its separate set of options\r\n     --no-alpn       Disable the ALPN TLS extension\r\n -N, --no-buffer     Disable buffering of the output stream\r\n     --no-keepalive  Disable TCP keepalive on the connection\r\n     --no-npn        Disable the NPN TLS extension\r\n     --no-sessionid  Disable SSL session-ID reusing\r\n     --noproxy <no-proxy-list> List of hosts which do not use proxy\r\n     --ntlm          Use HTTP NTLM authentication\r\n     --ntlm-wb       Use HTTP NTLM authentication with winbind\r\n     --oauth2-bearer <token> OAuth 2 Bearer Token\r\n -o, --output <file> Write to file instead of stdout\r\n     --pass <phrase> Pass phrase for the private key\r\n     --path-as-is    Do not squash .. sequences in URL path\r\n     --pinnedpubkey <hashes> FILE/HASHES Public key to verify peer against\r\n     --post301       Do not switch to GET after following a 301\r\n     --post302       Do not switch to GET after following a 302\r\n     --post303       Do not switch to GET after following a 303\r\n     --preproxy [protocol://]host[:port] Use this proxy first\r\n -#, --progress-bar  Display transfer progress as a bar\r\n     --proto <protocols> Enable/disable PROTOCOLS\r\n     --proto-default <protocol> Use PROTOCOL for any URL missing a scheme\r\n     --proto-redir <protocols> Enable/disable PROTOCOLS on redirect\r\n -x, --proxy [protocol://]host[:port] Use this proxy\r\n     --proxy-anyauth Pick any proxy authentication method\r\n     --proxy-basic   Use Basic authentication on the proxy\r\n     --proxy-cacert <file> CA certificate to verify peer against for proxy\r\n     --proxy-capath <dir> CA directory to verify peer against for proxy\r\n     --proxy-cert <cert[:passwd]> Set client certificate for proxy\r\n     --proxy-cert-type <type> Client certificate type for HTTS proxy\r\n     --proxy-ciphers <list> SSL ciphers to use for proxy\r\n     --proxy-crlfile <file> Set a CRL list for proxy\r\n     --proxy-digest  Use Digest authentication on the proxy\r\n     --proxy-header <header/@file> Pass custom header(s) to proxy\r\n     --proxy-insecure Do HTTPS proxy connections without verifying the proxy\r\n     --proxy-key <key> Private key for HTTPS proxy\r\n     --proxy-key-type <type> Private key file type for proxy\r\n     --proxy-negotiate Use HTTP Negotiate (SPNEGO) authentication on the proxy\r\n     --proxy-ntlm    Use NTLM authentication on the proxy\r\n     --proxy-pass <phrase> Pass phrase for the private key for HTTPS proxy\r\n     --proxy-service-name <name> SPNEGO proxy service name\r\n     --proxy-ssl-allow-beast Allow security flaw for interop for HTTPS proxy\r\n     --proxy-tlsauthtype <type> TLS authentication type for HTTPS proxy\r\n     --proxy-tlspassword <string> TLS password for HTTPS proxy\r\n     --proxy-tlsuser <name> TLS username for HTTPS proxy\r\n     --proxy-tlsv1   Use TLSv1 for HTTPS proxy\r\n -U, --proxy-user <user:password> Proxy user and password\r\n     --proxy1.0 <host[:port]> Use HTTP/1.0 proxy on given port\r\n -p, --proxytunnel   Operate through a HTTP proxy tunnel (using CONNECT)\r\n     --pubkey <key>  SSH Public key file name\r\n -Q, --quote         Send command(s) to server before transfer\r\n     --random-file <file> File for reading random data from\r\n -r, --range <range> Retrieve only the bytes within RANGE\r\n     --raw           Do HTTP "raw"; no transfer decoding\r\n -e, --referer <URL> Referrer URL\r\n -J, --remote-header-name Use the header-provided filename\r\n -O, --remote-name   Write output to a file named as the remote file\r\n     --remote-name-all Use the remote file name for all URLs\r\n -R, --remote-time   Set the remote file''s time on the local output\r\n -X, --request <command> Specify request command to use\r\n     --request-target Specify the target for this request\r\n     --resolve <host:port:address> Resolve the host+port to this address\r\n     --retry <num>   Retry request if transient problems occur\r\n     --retry-connrefused Retry on connection refused (use with --retry)\r\n     --retry-delay <seconds> Wait time between retries\r\n     --retry-max-time <seconds> Retry only within this period\r\n     --sasl-ir       Enable initial response in SASL authentication\r\n     --service-name <name> SPNEGO service name\r